						<div class="products_horisontal_layout">											<?php						$paged = (get_query_var('paged')) ? get_query_var('paged') : 1;						$current_term = get_term_by( 'slug', get_query_var( 'term' ), get_query_var( 'taxonomy' ) );						$args = array(							'orderby' => 'title',							'order' => ASC,							'tax_query' => array(								array(									'taxonomy' => 'product_type',									'field' => 'slug',									'terms' => $current_term								)							),							'posts_per_page' => 30,							'paged' => $paged						);						$wp_query = new WP_Query( $args );												while ( $wp_query->have_posts() ) : $wp_query->the_post(); ?>												<?php							$product_price = get_field('product_price');							$product_dimentions = get_field('product_dimentions');							$product_sizes = get_field('product_sizes');						?>						<div class="product group">							<div class="product_imagery">								<a href="<?php the_permalink(); ?>">								<?php if ( has_post_thumbnail()) : ?><?php the_post_thumbnail('horis_thumb'); ?><?php endif; ?>																<?php if($product_sizes){									echo '<div class="dimentions">										<img src="' . $product_sizes . '">									</div>';								} ?>								</a>							</div>							<div class="product_desc">								<span><strong>Модель:</strong> <?php the_title();?></span>								<span><?php if($product_dimentions){ echo '<strong>Размер:</strong> ' . $product_dimentions ; } ?></span>								<span class="product_price"><?php if($product_price){ echo '<strong>Цена:</strong> от ' . $product_price; } ?></span>								<a class="moar" href="<?php the_permalink(); ?>">Подробнее</a>							</div>						</div>													<?php endwhile;						// Reset Post Data						wp_reset_postdata();					?>						</div><!-- /.products_horisontal_layout -->